        "_id": "613f8896c1c0b3646447b0f7",
        "url": "https://remotive.io/remote-jobs/software-dev/senior-c-developer-891660",
        "title": "Senior C++ Developer",
        "company_name": "Flightradar24",
        "category": "Software Development",
