        "_id": "613f8896c1c0b3646447b18e",
        "url": "https://remotive.io/remote-jobs/data/data-product-owner-data-management-870311",
        "title": "Data Product Owner - Data Management ",
        "company_name": "Lendi",
        "category": "Data",
