        "_id": "613f8897c1c0b3646447b29b",
        "url": "https://remotive.io/remote-jobs/software-dev/senior-software-engineer-backend-858516",
        "title": "Senior Software Engineer, Backend",
        "company_name": "Magic Labs, Inc.",
        "category": "Software Development",
